import React, {useEffect, useState} from 'react'
import {useAppSelector, useAppDispatch} from '../store/hooks'
import {Card, message, Carousel, Image, Popconfirm} from 'antd'
import {EditOutlined, EllipsisOutlined, SettingOutlined, DeleteOutlined} from '@ant-design/icons';
import {IRoomData, ICameraData, IFrameData, IRoomInfo} from "../types/IRoomData.type";
import RoomService from "../services/RoomService";
import {RoomChart} from "./RoomChart";
import type { PopconfirmProps } from 'antd';
import axios from "axios";
import {removeRoom} from "../store/roomSlice";

const gridStyle: React.CSSProperties = {
    width: '100%',
    height: '100%',
    textAlign: 'center',
    alignItems: 'center'
};

const gridLabelStyle: React.CSSProperties = {
    width: '33%',
    height: '5%',
    textAlign: 'center',
    alignItems: 'center'
};

const gridContentStyle: React.CSSProperties = {
    width: '67%',
    height: '5%',
    textAlign: 'center',
    alignItems: 'center',
    margin: 'auto',
    backgroundColor: 'lightgreen',
}

const gridWarningContentStyle: React.CSSProperties = {
    width: '67%',
    height: '5%',
    textAlign: 'center',
    alignItems: 'center',
    margin: 'auto',
    backgroundColor: 'yellow',
}

const gridHighContentStyle: React.CSSProperties = {
    width: '67%',
    height: '5%',
    textAlign: 'center',
    alignItems: 'center',
    margin: 'auto',
    backgroundColor: 'red',
}

export function Room(roomData: IRoomInfo) {
    const dispatch = useAppDispatch()
    const _id = (roomData._id == null) ? '' : roomData._id

    const name = roomData.name
    const capacity: number = roomData.capacity
    const camera: Array<ICameraData> = roomData.camera == undefined ? [] : roomData.camera
    const activate: boolean = roomData.activate

    // const style_width_camera: string = ((100 -33) / camera.length).toString() + '%';

    const [images, setImages] = useState<Array<string>>([]);
    const [series, setSeries] = useState<Array<IFrameData>>([]);
    // const [date, setDate] = useState<Date>();
    // const [numberPeople, setNumberPeople] = useState<number>(0);
    // const [distribution, setDistribution] = useState<number>(0);

    let distribution: number = 0;
    let numberPeople: number = 0;
    let date: Date | null = null;

    const [open, setOpen] = useState(false);
    const [confirmLoading, setConfirmLoading] = useState(false);

    useEffect(() => {
        retrieveData();
        if (camera.length > 0)
            retrieveImages();
    }, []);


    const retrieveData = () => {
        RoomService.getAnalyzedRoom(_id)
            .then((response: any) => {
                console.log(response);
                setSeries(response.data.data);
            })
            .catch((e: Error) => {
                console.log(e);
            })
    }


    const retrieveImages = async () => {
        const objectURLImages: string[] = [];
        await Promise.all(
            camera.map(async (cam: ICameraData) => {
                await RoomService.getImageRoom(_id, cam.camera_id)
                    .then((response: any) => {
                        // const imageBlob = response.data;
                        const imageObjectURL = URL.createObjectURL(response.data);
                        objectURLImages.push(imageObjectURL);
                        // setImage(imageObjectURL);
                        // console.log(imageObjectURL)
                    })
                    .catch((e: Error) => {
                        objectURLImages.push('')
                        console.log(e);
                    });
            })
        )
        return setImages(objectURLImages);
        // console.log(objectURLImages);
    }

    const deleteRoom = () => {
        RoomService.deleteRoom(_id)
            .then((response: any) => {
                message.success("Deleted room");
                dispatch(removeRoom(roomData));
            })
            .catch((e: Error) => {
                if (axios.isAxiosError(e)) {
                    console.log(e);
                    message.error(`Could not delete room cause ${e.request.response}`);
                } else {
                    console.error(e);
                }
            });

    }

    const confirm: PopconfirmProps['onConfirm'] = (e) => {
        deleteRoom();
    };

    const cancel: PopconfirmProps['onCancel'] = (e) => {
    };



    if (series.length > 0) {
        numberPeople = series[series.length - 1].number_people;
        distribution = series[series.length - 1].rate;
        date = new Date(series[series.length - 1].time * 1000);
    }

    return (
        // <div style={{
        //     position: 'relative',
        //     width: "40vh",
        //     // height: "50vh",
        //     padding: '0px 20px',
        //     display: "flex",
        //     // flex-direction: "column"
        //     // margin: '20px 20px',
        // }}>
            <Card
                title={name}
                style={{
                    width: "100%",
                    padding: '0px 20px',
                    // margin: '20px 20px',
                }}
                hoverable={true}
                extra={<RoomChart activate={activate} camera={camera} capacity={capacity} name={name} _id={_id}/>}
                cover={
                    <Carousel autoplay={true} arrows infinite={false}>
                        {
                            camera.length > 0 ?
                            camera.map((cam: ICameraData, index: number) => {
                                return (
                                    <Card key={cam.camera_id} style={gridStyle}>
                                        <Image
                                            // width={400}
                                            height={300}
                                            alt={"Camera " + cam.camera_id}
                                            src={images[index]}
                                            preview={{src: images[index]}}
                                            fallback="data:image/png;base64,"
                                        >
                                        </Image>
                                    </Card>
                                )
                            }) : (<Card key="null" style={gridStyle}>
                                    <Image
                                        // width="100%"
                                        height={300}
                                        alt="No camera"
                                        // src=
                                        fallback="data:image/png;base64,"
                                    >
                                    </Image>
                                </Card>)
                        }
                    </Carousel>
                }
                actions={[
                    <SettingOutlined key="setting"/>,
                    <EditOutlined key="edit"/>,
                    <Popconfirm
                        title="Delete the task"
                        description="Are you sure to delete this task?"
                        onConfirm={confirm}
                        onCancel={cancel}
                        okText="Yes"
                        cancelText="No"
                    >
                        <DeleteOutlined key="ellipsis"/>
                    </Popconfirm>,
                ]}
            >

                <Card.Grid style={gridLabelStyle}>Date: {date == null ? "No record" : date?.toLocaleDateString('en-GB')}</Card.Grid>
                <Card.Grid style={numberPeople/capacity < 0.5 ? gridContentStyle: gridWarningContentStyle}>{numberPeople} / {capacity} </Card.Grid>
                <Card.Grid style={gridLabelStyle}>Time: {date == null ? "No record" : date?.toLocaleTimeString('it-IT')} </Card.Grid>
                <Card.Grid style={distribution < 50 ? gridContentStyle: gridWarningContentStyle}>{distribution} </Card.Grid>
                <Card.Grid style={gridLabelStyle}>Camera</Card.Grid>
                {
                    camera.length > 0 ?
                    camera.map((cam) => {
                        return (
                            <Card.Grid style={{
                                textAlign: 'center',
                                width: `${((100 - 33) / camera.length).toString() + '%'}`,
                            }}>
                                <a
                                    href={"https://" + cam.camera_ip}
                                    target="_blank"
                                >
                                    {cam.camera_id}
                                </a>
                            </Card.Grid>
                        )
                    }) : (
                            <Card.Grid style={{
                                textAlign: 'center',
                                width: `${(100 - 33).toString() + '%'}`,
                                color: "red"
                            }}>
                                No camera
                            </Card.Grid>
                        )
                }

                {/*<button onClick={() => {dispatch(updateRoom())}}>Update</button>*/}
            </Card>
        // </div>
    )
}
